import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import CardGroup from 'react-bootstrap/CardGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

export class Home extends Component {
    static displayName = Home.name;

    constructor(props) {
        super(props);
        this.state = {countries: [], loading: true};
    }

    componentDidMount() {
        this.getCountries();
    }

    static renderCountriesList(countries) {
        return (
            <CardGroup>
                <Row xs={1} md={3} className="g-4">
                    {countries.map(country =>
                        <Col>
                            <Card key={country.id}
                                  style={{width: '18rem'}}
                                  className="mb-2"
                                  bodyStyle={{padding: "0"}}>
                                <Card.Body>
                                    <Card.Title>{country.name}</Card.Title>
                                    <ListGroup className="list-group-flush">
                                        <ListGroup.Item>Country Code: {country.iso2Code}</ListGroup.Item>
                                        <ListGroup.Item>Region: {country.region}</ListGroup.Item>
                                        <ListGroup.Item>Admin Region: {country.adminRegion}</ListGroup.Item>
                                    </ListGroup>
                                </Card.Body>
                            </Card>
                        </Col>
                    )}
                </Row>
            </CardGroup>
        )
    }

    render() {
        let contents = this.state.loading
            ? <p><em>Loading...</em></p>
            : Home.renderCountriesList(this.state.countries);

        return (
            <div>
                <h1 id="tabelLabel">Weather forecast</h1>
                <p>This component demonstrates fetching data from the server.</p>
                {contents}
            </div>
        );
    }

    async getCountries() {
        const response = await fetch('/api/countries/getCountries');
        const data = await response.json();
        console.log(data)
        this.setState({countries: data, loading: false});
    }
}
